import React, { Component } from 'react';
import ReactNipple from 'react-nipple';
import ROSLIB from 'roslib';
import Config from "./config";

class Joystck extends Component {

    state = {
		ros : null
	};
	constructor(){
		super();
		this.init_connection();

        this.move = this.move.bind(this);
	}

	init_connection(){
		// eslint-disable-next-line
		this.state.ros = new ROSLIB.Ros();

		this.state.ros.on("connection", () => {
			console.info("Connected to ROS:TELEOP");
		});

		this.state.ros.on("close", () => {
			console.warn("Disconnected from ROS:TELEOP");
			setTimeout(()=>{
				try{
					this.state.ros.connect(
						"ws://"+Config.ROSBRIDGE_SERVER_IP+":"+Config.ROSBRIDGE_SERVER_PORT+""
					);
				}catch(error){
					console.error("Connection problem : TELEOP");
				}
			},Config.RECONNECTION_TIMER);
		});

		this.state.ros.on("error", (error) => {});

		try{
			this.state.ros.connect(
				"ws://"+Config.ROSBRIDGE_SERVER_IP+":"+Config.ROSBRIDGE_SERVER_PORT+""
			);
		}catch(error){
			console.error("Connection problem : TELEOP");
		}
	}
    move(evt, data) {

        max_linear = 5.0; // m/s
        max_angular = 2.0; // rad/s
        max_distance = 75.0; // pixels;
        linear_speed = Math.sin(data.angle.radian) * max_linear * data.distance/max_distance;
        angular_speed = -Math.cos(data.angle.radian) * max_angular * data.distance/max_distance;
    }

    render() {
        return (
            <div>
                <ReactNipple
                    options={{ mode: 'static', position: { top: '50%', left: '50%' }, color: '#0066ff', size: 150 }}
                    style={{
                        width: 150,
                        height: 150,
                        position: 'relative'
                    }}
                    onMove={(evt, data) => console.log(evt, data)}
                    />
            </div>
        );
    }
}

export default Joystck;
